const soundButton=document.querySelector(".sound-button"),crossIcon=document.querySelector(".cross-icon"),continueButton=document.querySelector(".continue-button"),firstPage=document.querySelector(".first-page"),secondPage=document.querySelector(".second-page"),nextButton=document.querySelector(".next-button"),thirdPage=document.querySelector(".third-page"),slides=[{img:"../images/src/slider-1.png",text:"Step-sister"},{img:"../images/src/slider-2.png",text:"Your Neighbour"},{img:"../images/src/slider-3.png",text:"Famous Celebrity"}];let currentSlide=0;const sliderImage=document.querySelector(".slider-image"),sliderText=document.querySelector(".slider-text"),leftArrow=document.querySelector(".left-arrow"),rightArrow=document.querySelector(".right-arrow");function updateSlide(e){sliderImage.src=slides[e].img,sliderText.textContent=slides[e].text}crossIcon.style.display="none",soundButton.addEventListener("click",(()=>{crossIcon.style.display="none"===crossIcon.style.display?"block":"none"})),continueButton.addEventListener("click",(()=>{firstPage.style.display="none",secondPage.style.display="flex"})),nextButton.addEventListener("click",(()=>{secondPage.style.display="none",thirdPage.style.display="flex"})),leftArrow.addEventListener("click",(()=>{currentSlide=0===currentSlide?slides.length-1:currentSlide-1,updateSlide(currentSlide)})),rightArrow.addEventListener("click",(()=>{currentSlide=currentSlide===slides.length-1?0:currentSlide+1,updateSlide(currentSlide)}));